import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {
    articles=[];
    constructor(){
        super();
        this.state={
            articles: this.articles,
            loading: false,
            page:1
        };
    }
    prevClick(){
        console.log("Previous");
    }
    nextClick(){
        console.log("Next");
    }
async componentDidMount(){
    let url="https://newsapi.org/v2/top-headlines?country=in&apiKey=127c81290a0b49bc94b76cb4838eeea9&page=1";
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({articles : parsedData.articles});
}
  render() {
    return (
        <div className="container my-3">
            <h1>NewsMonkey- Top Headlines</h1>
            <div className="row">
            {
                this.state.articles.map((ele)=>{
                return(
            <div className="col-lg-4 my-3" key={ele.url?ele.url:"/"}>
                <NewsItem title={ele.title?ele.title.slice(0,50):" "} description={ele.description?ele.description.slice(0,90):" "} img_url={ele.urlToImage?ele.urlToImage:"https://c.ndtvimg.com/2022-06/jlikupmg_virat-kohli-806_625x300_22_June_22.jpg"} newsUrl={ele.url?ele.url:"/"}/>    
            </div>
                )
            })}
            </div>
            <div className="container d-flex justify-content-between">
            <button onClick={prevClick} type="button" disabled={this.state.page<=1} class="btn btn-dark">&larr; Previous</button>
            <button onClick={nextClick} type="button" class="btn btn-dark">next &rarr;</button>
            </div>
      </div>
    )
  }
}
