import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Spinner from './spinner'
import PropTypes from 'prop-types'
import InfiniteScroll from 'react-infinite-scroll-component';
export default class News extends Component {
    static defaultProps = {
        country:"in",
        pageSize: 6,
        category: "general"
    }
    static propTypes={
        name:PropTypes.string,
        page:PropTypes.number,
        category:PropTypes.string
    }
    capitalizeFirstLetter=(string)=> {
        return string.charAt(0).toUpperCase() + string.slice(1);
      }
    articles=[];
    constructor(props){
        super(props);
        this.state={
            articles: this.articles,
            loading: false,
            page: 1
        };
        document.title="MyNews - "+ this.capitalizeFirstLetter(this.props.category);
    }
    prevClick  = async()=>{
        let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=8abb9d45f47347d38920e57804efcdcd&page=${this.state.page-1}&pageSize=${this.props.pageSize}`;
        this.setState({loading:true});
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({
            page : this.state.page-1,
            articles : parsedData.articles,
            loading:false
        })
    }
     nextClick = async()=>{
        let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=8abb9d45f47347d38920e57804efcdcd&page=${this.state.page+1}&pageSize=${this.props.pageSize}`;
        this.setState({loading:true});
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({
            page : this.state.page+1,
            articles : parsedData.articles,
            loading:false
        })
    }
async componentDidMount(){
    let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=8abb9d45f47347d38920e57804efcdcd&page=${this.state.page}&pageSize=${this.props.pageSize}`;
    this.setState({loading:true});
    let data = await fetch(url);
    let parsedData = await data.json();
    this.setState({articles : parsedData.articles,totalarticles:parsedData.totalResults,loading:false});
}
  render() {
    return (
        <div className="container">
            <h1 className="text-center" style={{margin:"40px"}}>MyNews- Top Headlines from {this.capitalizeFirstLetter(this.props.category)} Headlines</h1>
            <div className="row">
                <InfiniteScroll dataLength={this.state.articles.length} next={this.fetchMoreData} hasMore={this.state.articles.length} loader={<h4>Loading...</h4>}>
                {this.state.articles.map((ele)=>{
                return(
                    <div className="col-lg-4 my-3" key={ele.url?ele.url:"/"}>
                        <NewsItem title={ele.title?ele.title:" "} description={ele.description?ele.description.slice(0,90):" "}
                        img_url={ele.urlToImage?ele.urlToImage:"https://c.ndtvimg.com/2022-06/jlikupmg_virat-kohli-806_625x300_22_June_22.jpg"} 
                        newsUrl={ele.url?ele.url:"/"} author={ele.author?ele.author:"Anonymous"} date={ele.publishedAt} source={ele.source.name}/>    
                    </div>)
                })}
            </InfiniteScroll>
            </div>
            <div className="container d-flex justify-content-between">
            <button onClick={this.prevClick} type="button" disabled={this.state.page<=1} className="btn btn-dark">&larr; Previous</button>
            <button onClick={this.nextClick} type="button" disabled={this.state.page+1>Math.ceil(this.state.totalarticles/this.props.pageSize)}className="btn btn-dark">next &rarr;</button>
            </div>
      </div>
    )
  }
}
